num = ''
summ = 1
loop = 0
result = 0
i = 0
mylist = []
result_list = []
numlist = []
while i == 0:
    for n in num:
        n = int(n)
        mylist.append(n)
        if len(mylist) == 8:
            summ = 1
            for j in mylist:
                summ = summ*j
            result_list.append(summ)

            if loop == 1 :
                if summ == result:
                    for k in mylist:
                        numlist.append(str(k))
                        i = 1
            mylist.pop(0)

    result = max(result_list)
    loop = 1
result_list.sort()
print(f'{numlist[0]} * {numlist[1]} * {numlist[2]} * {numlist[3]} * {numlist[4]} * {numlist[5]} * {numlist[6]} * {numlist[7]} = {result_list[-1]}') 
