import React, { useEffect, useState} from "react";

const EpisodesModal = ({ active, handleModal, handleEpisodeModal, episode_id, setErrorMessage }) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [video_url, setVideo_url] = useState("");
    const [thumbnail_url, setThumbnail_url] = useState("");
    const [release_date, setRelease_date] = useState("");
    const [season_id, setSeasonId] = useState("");

    useEffect(() => {
        const getEpisode = async () => {
            const requestOptions = {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                },
            };
            const response = await fetch(`/api/episodes/${episode_id}`, requestOptions);

            if (!response.ok) {
                setErrorMessage("Could not get the episode");
            } else {
                const data = await response.json();
                setTitle(data.title);
                setDescription(data.description);
                setVideo_url(data.video_url);
                setThumbnail_url(data.thumbnail_url);
                setRelease_date(data.release_date);
                setSeasonId(data.season_id);
            }
        };

        if (episode_id) {
            getEpisode();
        }
    }, [episode_id]);

    const cleanFormData = () => {
        setTitle("");
        setDescription("");
        setVideo_url("");
        setThumbnail_url("");
        setRelease_date("");
        setSeasonId("");
    }

    const handleCreateEpisode = async (e) => {
        e.preventDefault();
        const requestOptions = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                title: title,
                description: description,
                video_url: video_url,
                thumbnail_url: thumbnail_url,
                season_id: season_id,
            }),
        };
        const response = await fetch("/api/episodes", requestOptions);
        if (!response.ok) {
            setErrorMessage("Could not create the episode");
        } else {
            cleanFormData();
            handleModal();
        }
    };

    const handleUpdateEpisode = async (e) => {
        e.preventDefault();
        const requestOptions = {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                title: title,
                description: description,
                video_url: video_url,
                thumbnail_url: thumbnail_url,
                release_date: release_date,
                season_id: season_id,
            }),
        };
        const response = await fetch(`/api/episodes/${episode_id}`, requestOptions);
        if (!response.ok) {
            setErrorMessage("Could not update the episode");
        } else {
            const data = await response.json();
            setTitle(data.title);
            setDescription(data.description);
            setVideo_url(data.video_url);
            setThumbnail_url(data.thumbnail_url);
            setRelease_date(data.release_date);
            setSeasonId(data.season_id);

            handleModal();
        }
    }

    return (
        <div className={`modal ${active ? "is-active" : ""}`}>
            <div className="modal-background" onClick={handleEpisodeModal}></div>
            <div className="modal-card">
                <header className="modal-card-head">
                    <p className="modal-card-title">Episode</p>
                    <button className="delete" aria-label="close" onClick={handleEpisodeModal}></button>
                </header>
                <section className="modal-card-body">
                    <form>
                        <div className="field">
                            <label className="label">Title</label>
                            <div className="control">
                                <input
                                    className="input"
                                    type="text"
                                    placeholder="Title"
                                    value={title}
                                    onChange={(e) => setTitle(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="field">
                            <label className="label">Description</label>
                            <div className="control">
                                <input
                                    className="input"
                                    type="text"
                                    placeholder="Description"
                                    value={description}
                                    onChange={(e) => setDescription(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="field">
                            <label className="label">Video URL</label>
                            <div className="control">
                                <input
                                    className="input"
                                    type="text"
                                    placeholder="Video URL"
                                    value={video_url}
                                    onChange={(e) => setVideo_url(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="field">
                            <label className="label">Thumbnail URL</label>
                            <div className="control">
                                <input
                                    className="input"
                                    type="text"
                                    placeholder="Thumbnail URL"
                                    value={thumbnail_url}
                                    onChange={(e) => setThumbnail_url(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="field">
                            <label className="label">Season ID</label>
                            <div className="control">
                                <input
                                    className="input"
                                    type="text"
                                    placeholder="Season ID"
                                    value={season_id}
                                    onChange={(e) => setSeasonId(e.target.value)}
                                />
                            </div>
                        </div>
                    </form>
                </section>
                <footer className="modal-card-foot">
                    {episode_id ? (
                    <button className="button is-success" onClick={handleCreateEpisode}>
                        Create
                    </button>
                    ) : (
                    <button className="button" onClick={() => { cleanFormData(); handleEpisodeModal();}}>
                        Cancel
                    </button>
                    )}
                </footer>
            </div>
        </div>


    );

};
export default EpisodesModal;
