import React, { useState, useEffect } from "react";
import axios from "axios";

const Movie = ({ match }) => {
    const [movie, setMovie] = React.useState({});


    useEffect(() => {
        const getMovie = async () => {
            const { data } = await axios.get(`/api/movies/${match.params.id}`);
            setMovie(data);
        };
        getMovie();
    }, [match]);
    

};

export default Movie;
