import React, { useState, useEffect, useContext } from "react";
import ErrorMessage from "./ErrorMessage";
import moment from "moment";
import { UserContext } from "../context/UserContext";


const Table = () => {
    const [token, setToken] = useContext(UserContext);
    const [series, setSeries] = useState([]);

    const [activeModal, setActiveModal] = useState(false);
    const [loaded, setLoaded] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");
    const [id, setId] = useState(null);

    useEffect(() => {
      getSeries();
    }, []);

        const getSeries = async () => {
            const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
        };

        const response = await fetch("/api/series", requestOptions);
        if (!response.ok) {
            setErrorMessage("Something went wrong. Couldn't load the Series");
        } else {
            const data = await response.json();
            setSeries(data);
            setLoaded(true);
        }
    };

        const handleLogout = () => {
            setToken(null);
        };
        const handleUpdate = async (id) => {
            setId(id);
            setActiveModal(true);
        };

        const handleDelete = async (id) => {
            const requestOptions = {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
            };
            const response = await fetch(`/api/leads/${id}`, requestOptions);
            if (!response.ok) {
            setErrorMessage("Failed to delete lead");
            }

            getLeads();
        };


    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-12">
                        <h1 className="text-center">Series Table</h1>
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">SeriesID</th>
                                    <th scope="col">Title</th>
                                    <th scope="col">Description</th>
                                    <th scope="col">Date Released</th>
                                    <th scope="col">Cover Image</th>
                                </tr>
                            </thead>
                            <tbody>
                                {loaded && series.map((series) => (
                                    <tr key={series.series_id}>
                                        <th scope="row">{series.series_id}</th>
                                        <td>{series.title}</td>
                                        <td>{series.description}</td>
                                        <td>{moment(series.release_date).format('ll')}</td>
                                        <td>{series.cover_image}</td>
                                        <td>
                                        <button
                                            className="button mr-2 is-info is-light"
                                            onClick={() => handleUpdate(series.seriesid)}
                                        >
                                            Update
                                        </button>
                                        <button
                                            className="button mr-2 is-danger is-light"
                                            onClick={() => handleDelete(series.series_id)}
                                        >
                                            Delete
                                        </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
                {token && (
                            <button className="button" onClick={handleLogout}>
                            Logout
                            </button>
                        )}        
            </div>


        </>
    );
};

export default Table;
