import React, { useContex, useEffect, useState } from "react";
import moment from "moment";

import ErrorMessage from "./ErrorMessage";
import SeriesModal from "./SeriesModal";
import { UserContext } from "../context/UserContext";

const Table = () => {
    //const [token] = UserContext(UserContext);
    const [series, setSeries] = useState(null);


    const [errorMessage, setErrorMessage] = useState("");
    const [loaded, setLoaded] = useState(false);
    const [activeModal, setActiveModal] = useState("");
    const [series_id, setSereseries_id] = useState(null);

    const handleCreate = () => {

        setSereseries_id(null);
        setActiveModal(true);

    };

    const handleUpdate = async (series_id) => {
        setSereseries_id(series_id);
        setActiveModal(true);
    };

    const handleDelete = async (series_id) => {
        const requestOptions = {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch(`/api/series/${series_id}`, requestOptions);
        if (!response.ok) {
            setErrorMessage("Failed to delete series");
        }

        getSeries();
    };

    const getSeries = async () => {
        const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch("/api/series", requestOptions);
        if (!response.ok) {
            setErrorMessage("Something went wrong. Couldn't load the series");
        } else {
            const data = await response.json();
            setSeries(data);
            setLoaded(true);
        }
    };

    useEffect(() => {
        getSeries();
    }, []);

    const handleModal = () => {
        setActiveModal(!activeModal);
        getSeries();
        setSereseries_id(null);
    };

    return (
        <>
            <SeriesModal
                active={activeModal}
                handleModal={handleModal}
                series_id={series_id}
                setErrorMessage={setErrorMessage}
            />

            { /* SERIES TABLE */}

            <div className="columns is-mobile is-centered">
            <div className="column">
                <p className="bd-notification is-primary">
                    <h1 style={{paddingLeft:20, fontWeight:"bolder", fontSize:40}}>Series Table</h1>
                </p>
            </div>
            </div>

            <button 
                className="button is-fullwidth is-success is-focused"

                onClick={() => handleCreate()}
            >
                Create Series
            </button>
            <ErrorMessage message={errorMessage} />
            {loaded && series ? (
                <table className="table is-fullwidth is-hoverable">
                    <thead>
                        <tr>
                            <th>Seire ID</th>
                            <th>Title</th>
                            <th>Descripton</th>
                            <th>Release Date</th>
                            <th>Image Cover</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {series.map((series) => (
                            <tr key={series.series_id}>
                                <td>{series.title}</td>
                                <td>{series.description}</td>
                                <td>{series.release_date}</td>
                                <td>{series.cover_image}</td>
                                <td>
                                    <button
                                        className="button is-primary is-small mr-2"
                                        onClick={() => handleUpdate(series.series_id)}
                                    >
                                        Update
                                    </button>
                                    <button
                                        className="button is-danger is-small"
                                        onClick={() => handleDelete(series.series_id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}

            <hr/>

            { /* ENDSERIES TABLE */}



        { /* SEASON TABLE */}
        <div className="columns is-mobile is-centered">
            <div className="column">
                <p className="bd-notification is-primary">
                    <h1 style={{paddingLeft:20, fontWeight:"bolder", fontSize:40}}>Series {"<<"} Season Table</h1>
                </p>
            </div>
            </div>

            <button 
                className="button is-fullwidth is-success is-focused"

                onClick={() => handleCreate()}
            >
                Create Series
            </button>
            <ErrorMessage message={errorMessage} />
            {loaded && season ? (
                <table className="table is-fullwidth is-hoverable">
                    <thead>
                        <tr><th></th>
                            <th>Title</th>
                            <th>Descripton</th>
                            <th>Release Date</th>
                            <th>Image Cover</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {season.map((season) => (
                            <tr key={season.season_id}>
                                <td>{season.title}</td>
                                <td>{season.release_date}</td>
                                <td>{season.season_id}</td>
                                <td>
                                    <button
                                        className="button is-primary is-small mr-2"
                                        onClick={() => handleUpdate(season.season_id)}
                                    >
                                        Update
                                    </button>
                                    <button
                                        className="button is-danger is-small"
                                        onClick={() => handleDelete(season.season_id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}

            <hr/>

            { /* ENDSEASON TABLE */}

        { /* EPISODE TABLE */}
        <div className="columns is-mobile is-centered">
            <div className="column">
                <p className="bd-notification is-primary">
                    <h1 style={{paddingLeft:20, fontWeight:"bolder", fontSize:40}}>Series {"<<"} Season {"<<"} Episode Table</h1>
                </p>
            </div>
            </div>

            <button 
                className="button is-fullwidth is-success is-focused"

                onClick={() => handleCreate()}
            >
                Create Series
            </button>
            <ErrorMessage message={errorMessage} />
            {loaded && episodes ? (
                <table className="table is-fullwidth is-hoverable">
                    <thead>
                        <tr>

                            <th>Title</th>
                            <th>Descripton</th>
                            <th>Release Date</th>
                            <th>Image Cover</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {episodes.map((episodes) => (
                            <tr key={episodes.series_series_id}>
                                <td>{episodes.title}</td>
                                <td>{episodes.description}</td>
                                <td>{episodes.release_date}</td>
                                <td>{episodes.cover_image}</td>
                                <td>
                                    <button
                                        className="button is-primary is-small mr-2"
                                        onClick={() => handleUpdate(series.series_id)}
                                    >
                                        Update
                                    </button>
                                    <button
                                        className="button is-danger is-small"
                                        onClick={() => handleDelete(series.series_id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}

            <hr/>

            { /* END EPISODE TABLE */}
        </>
    );
};

export default Table;
