import React, { useContext, useEffect, useState } from "react";
import moment from "moment";

import ErrorMessage from "./ErrorMessage";
import SeriesModal from "./SeriesModal";
import { UserContext } from "../context/UserContext";

const Table = () => {
    //const [token] = UserContext(UserContext);
    const [series, setSeries] = useState(null);


    const [errorMessage, setErrorMessage] = useState("");
    const [loaded, setLoaded] = useState(false);
    const [activeModal, setActiveModal] = useState(false);
    const [seriesId, setSeriesId] = useState(null);

    const handleUpdate = async (seriesId) => {
        setSeriesId(seriesId);
        setActiveModal(true);
    };


    const handleDelete = async (seriesId) => {
        const requestOptions = {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch(`/api/series/${seriesId}`, requestOptions);
        if (!response.ok) {
            setErrorMessage("Failed to delete series");
        }

        getSeries();
    };

    const getSeries = async () => {
        const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch("/api/series", requestOptions);
        if (!response.ok) {
            setErrorMessage("Something went wrong. Couldn't load the series");
        } else {
            const data = await response.json();
            setSeries(data);
            setLoaded(true);
        }
    };

    useEffect(() => {
        getSeries();
    }, []);

    const handleModal = () => {
        setActiveModal(!activeModal);
        getSeries();
        setSereseriesId(null);
    };

    return (
        <>  

            <SeriesModal
                active={activeModal}
                handleModal={handleModal}
                series_id={seriesId}
                setErrorMessage={setErrorMessage}
            />


            <div className="columns is-mobile is-centered">
            <div className="column">
                <p className="bd-notification is-primary">
                    <h1 style={{paddingLeft:20, fontWeight:"bolder", fontSize:40}}>Series Table</h1>
                </p>
            </div>
            </div>

            <button 
                className="button is-fullwidth is-success is-focused"

                onClick={() => setActiveModal(true)}
            >
                Create Series
            </button>
            <ErrorMessage message={errorMessage} />
            {loaded && series ? (
                <table className="table is-fullwidth is-hoverable">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Descripton</th>
                            <th>Release Date</th>
                            <th>Image Cover</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {series.map((series) => (
                            <tr key={series.series_id}>
                                <td>{series.title}</td>
                                <td>{series.description}</td>
                                <td>{moment(series.release_date).format("MMM Do YY")}</td>
                                <td>{series.cover_image}</td>
                                <td>
                                    <button
                                        className="button is-primary is-small mr-2"
                                        onClick={() => handleUpdate(series.series_id)}
                                    >
                                        Update
                                    </button>
                                    <button
                                        className="button is-danger is-small"
                                        onClick={() => handleDelete(series.series_id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}
        </>
    );
};

export default Table;
