import React, { useEffect, useState } from "react";

const LeadModal = ({ active, handleModal, token, id, setErrorMessage }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [company, setCompany] = useState("");
    const [email, setEmail] = useState("");
    const [note, setNote] = useState("");

    useEffect(() => {
        const getLead = async () => {
            const requestOptions = {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: "Bearer " + token,
                },
            };
            const response = await fetch(`/api/leads/${id}`, requestOptions);

            if (!response.ok) {
                setErrorMessage("ไม่สามารถเรียกข้อมูลได้!..")
            } else {
                const data = await response.json();
                setFirstName(data)
            }
        };
    }, []);

    const cleanformData = () => {
        setFirstName("");
        setLastName("");
        setCompany("")
        setEmail("");
        setNote("");
    }

    const handleCreateLead = async (e) => {
        e.preventDefault();
        const requestOptions = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + token,
            },
            body: JSON.stringify({
                first_name: firstName,
                last_name: lastName,
                company: company,
                email: email,
                note: note,
            }),
        };
        const response = await fetch("/api/leads", requestOptions);
        if (!response.ok) {
            setErrorMessage("มีบางอย่างเกิดขึ้นขณะสร้าง Account ");
        } else {
            cleanformData();
            handleModal();
        }
    }; 
    
    return (
        <div className={`modal ${active && "is-active"}`}>
        <div className="modal-background" onClick={handleModal}></div>
            <div className="modal-card">
                <header className="modal-card-head has-background-netflix">
                <h1 className="modal-card-title">
                    {id ? "Update Lead" : "Create Lead"}
                </h1>
                </header>
                <section className="modal-card-body">
                <form>
                    <div className="field">
                    <label className="label">First Name</label>
                    <div className="control">
                        <input
                        type="text"
                        placeholder="Enter first name"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        className="input"
                        required
                        />
                    </div>
                    </div>
                    <div className="field">
                    <label className="label">Last Name</label>
                    <div className="control">
                        <input
                        type="text"
                        placeholder="Enter last name"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        className="input"
                        required
                        />
                    </div>
                    </div>
                    <div className="field">
                    <label className="label">Company</label>
                    <div className="control">
                        <input
                        type="text"
                        placeholder="Enter company"
                        value={company}
                        onChange={(e) => setCompany(e.target.value)}
                        className="input"
                        />
                    </div>
                    </div>
                    <div className="field">
                    <label className="label">Email</label>
                    <div className="control">
                        <input
                        type="email"
                        placeholder="Enter email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="input"
                        />
                    </div>
                    </div>
                    <div className="field">
                    <label className="label">Note</label>
                    <div className="control">
                        <input
                        type="text"
                        placeholder="Enter note"
                        value={note}
                        onChange={(e) => setNote(e.target.value)}
                        className="input"
                        />
                    </div>
                    </div>
                </form>
                </section>
                <footer className="modal-card-foot has-background-primary-light">
                    {id ? (
                    <button className="button is-info">Update</button>
                    ) : (
                    <button className="button is-primary" onClick={handleCreateLead}>Create
                    </button>
                    )}
                    <button className="button is-danger" onClick={() => {cleanformData(); handleModal();}}>Cancel</button>

                </footer>
            </div>
        </div>
  );
};

export default LeadModal;
