import React, { useEffect, useState } from "react";

const MovieGenreModal = ({ active, handleModal, handleMovieGenreModal, movie_genre_id, setErrorMessage }) => {
    
    const [movie_id, SetMovieId] = useState("");
    const [genre_id, SetGenreId] = useState("");

    useEffect(() => {
        const getGenres = async () => {
            const requestOptions = {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                },
            };
            const response = await fetch(`/api/genres/${movie_genre_id}`, requestOptions);

            if (!response.ok){
                setErrorMessage("Could not get genre");
            } else {
                const data = await response.json();
                setMovieId(data.movie_id);
                setGenreId(data.genre_id);
                
            }
        }
        if (movie_genre_id){
            getGenres();
        }
    }, [movie_genre_id]);

    const cleanFormData = () => {
        setMovieId("");
        setGenreId("");
    }

    const handleCreateMovieGenres = async (e) => {
        e.preventDefault();
        const requestOptions = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                genre_name: genre_name,
            }),
        };
        const response = await fetch("/api/genres", requestOptions);
        if (!response.ok){
            setErrorMessage("Could not create the genre");
        } else {
            cleanFormData();
            handleModal();
        }
    }

    const handleUpdateMovieGenre = async (e) => {
        e.preventDefault();
        const requestOptions = {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                genre_name: genre_name,
                
            }),
        };
        const response = await fetch(`/api/genres/${movie_genre_id}`, requestOptions);
        if (!response.ok){
            setErrorMessage("Could not update the genre");
        } else {
            cleanFormData();
            handleModal();
        }
    }

    return (
        <div className={`modal ${active ? "is-active" : ""}`}>
            <div className="modal-background" onClick={handleModal}></div>
            <div className="modal-card">
                <header className="modal-card-head">
                    <p className="modal-card-title">{movie_genre_id ? "Update" : "Create"} Genre</p>
                    <button className="delete" aria-label="close" onClick={handleModal}></button>
                </header>
                <section className="modal-card-body">
                    <form onSubmit={movie_genre_id ? handleUpdateMovieGenre : handleCreateMovieGenres}>
                        <div className="field">
                            <label className="label">Genre Name</label>
                            <div className="control">
                                <input
                                    className="input"
                                    type="text"
                                    placeholder="Genre Name"
                                    value={genre_name}
                                    onChange={(e) => SetGenreName(e.target.value)}
                                />
                            </div>
                        </div>
                        <div className="field is-grouped">
                            <div className="control">
                                <button className="button is-link" type="submit">
                                    {movie_genre_id ? "Update" : "Create"}
                                </button>
                            </div>
                            <div className="control">
                                <button className="button is-link is-light" onClick={handleModal}>
                                    Cancel
                                </button>
                            </div>
                        </div>
                    </form>
                </section>
            </div>
        </div>
    );
};

export default MovieGenreModal;
