import React, { useContext, useEffect, useState } from "react";
import moment from "moment";

import ErrorMessage from "./ErrorMessage";
import { UserContext } from "../context/UserContext";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { FaPen, FaPencilAlt } from 'react-icons/fa';

const Table = () => {
    const [token] = useContext(UserContext);x
    const [leads, setLeads] = useState(null);
    const [errorMessage, setErrorMessage] = useState("");
    const [loaded, setLoaded] = useState(false);
    const [activeModal, setActiveModal] = useState(false);
    const [id, setId] = useState(null);

    const getLeads = async () => { 
        const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + token,
            },
        };
        const response = await fetch("/api/leads", requestOptions);
        if (!response.ok) {
            setErrorMessage("อ๊ะ!! มีบางอย่างผิดพลาด ไม่สามารถโหลดตารางได้");
        } else {
            const data = await response.json();
            setLeads(data);
            setLoaded(true);
        }
    };

    useEffect(() => {
        getLeads();
    }, []);
    
    return (
        <>
            <button className="button is-fullwidth mb-5 is-primary">
                Create Lead
            </button>
                <ErrorMessage message={errorMessage} />
                {loaded && leads ? (
                        <table className="table is-fullwidth">
                            <thead>
                                <tr>
                                    <th>First Name</th>
                                    <th>Last Name</th>
                                    <th>Company</th>
                                    <th>Email</th>
                                    <th>Note</th>
                                    <th>Last Updated</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {leads.map((lead) => (
                                <tr key={lead.id}>
                                    <td>{lead.first_name}</td>
                                    <td>{lead.last_name}</td>
                                    <td>{lead.company}</td>
                                    <td>{lead.email}</td>
                                    <td>{lead.note}</td>
                                    <td>{moment(lead.date_last_updated).format("MMM Do YY")}</td>
                                    <td>
                                        <div style={{ display: 'flex', gap: '20px' }}>
                                        <button className="button is-fullwidth mb-5 is-primary">
                                            <FaPen style={{ color: '#e90c0e' }} />
                                        </button>
                                        <button className="button is-fullwidth mb-5">
                                            <FaPencilAlt style={{ color: '#e90c0e' }} />
                                        </button>
                                        </div>
                                    </td>
                                </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                <p>Loading...</p>
            )}
        </>
    );
};

export default Table;
