import React, { useContext, useEffect, useState } from "react";
import moment from "moment";

import ErrorMessage from "./ErrorMessage";
import { UserContext } from "../context/UserContext";

const Table = () => {
    const [token] = useContext(UserContext);
    const [leads, setLeads] = useState(null);
    const [errorMessage, setErrorMessage] = useState("");
    const [loaded, setLoaded] = useState(false);
    const [activeModal, setActiveModal] = useState(false);
    const [id, setId] = useState(null);

    const getLeads = async () => { 
        const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + token,
            },
        };
        const response = await fetch("/api/leads", requestOptions);
        if (!response.ok) {
            setErrorMessage("อ๊ะ!! มีบางอย่างผิดพลาด ไม่สามารถโหลดตารางได้");
        } else {
            const data = await response.json();
            setLeads(data)
            setLoaded(true);
        }
    };

    useEffect(() => {
        getLeads();
    }, [token]);

    return (
        <>
            <button className="button is-fullwidth mb-5 is-primary">
                Create Lead
            </button>
                <ErrorMessage message={errorMessage} />
                {loaded && leads ? (
                        <table className="table is-fullwidth">
                            <thead>
                                <tr>
                                    <th>First Name</th>
                                    <th>Last Name</th>
                                    <th>Company</th>
                                    <th>Email</th>
                                    <th>Note</th>
                                    <th>Last Updated</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {leads.map((lead) => (
                                    <tr key={lead.id}>
                                        <td>{lead.first_name}</td>
                                        <td>{lead.last_name}</td>
                                        <td>{lead.company}</td>
                                        <td>{lead.email}</td>
                                        <td>{lead.note}</td>
                                        <td>{moment(lead.updated_at).format("YYYY-MM-DD HH:mm:ss")}</td>
                                        <td>
                                            <button className="button is-small is-danger">Delete</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                <p>Loading...</p>
            )}
        </>
    );
};

export default Table;
