import React, { useContex, useEffect, useState } from "react";
import moment from "moment";

import ErrorMessage from "./ErrorMessage";
import SeriesModal from "./SeriesModal";
import SeasonsModal from "./SeasonsModal";

import { UserContext } from "../context/UserContext";

const Table = () => {
    //const [token] = UserContext(UserContext);
    const [series, setSeries] = useState(null);



    const [errorMessage, setErrorMessage] = useState("");
    const [loaded, setLoaded] = useState(false);
    const [activeModal, setActiveModal] = useState("");
    const [series_id, setSeries_id] = useState(null);

    const handleCreate = () => {

        setSeries_id(null);
        setActiveModal(true);

    };

    const handleUpdate = async (series_id) => {
        setSeries_id(series_id);
        setActiveModal(true);
    };

    const handleDelete = async (series_id) => {
        const requestOptions = {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch(`/api/series/${series_id}`, requestOptions);
        if (!response.ok) {
            setErrorMessage("Failed to delete series");
        }

        getSeries();
    };

    const getSeries = async () => {
        const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch("/api/series", requestOptions);
        if (!response.ok) {
            setErrorMessage("Something went wrong. Couldn't load the series");
        } else {
            const data = await response.json();
            setSeries(data);
            setLoaded(true);
        }
    };

    //SEASON
    const [seasons, setSeasons] = useState(null);
    const [season_id, setSeason_id] = useState(null);

    const handleCreateSeason = () => {
        setSeason_id(null);
        setActiveModal(true);
    };

    const handleUpdateSeason = async (season_id) => {
        setSeason_id(season_id);
        setActiveModal(true);
    };

    const handleDeleteSeason = async (season_id) => {
        const requestOptions = {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch(`/api/seasons/${season_id}`, requestOptions);
        if (!response.ok) {
            setErrorMessage("Failed to delete season");
        }

        getSeasons();
    };

    const getSeasons = async () => {
        const requestOptions = {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
        };
        const response = await fetch("/api/seasons", requestOptions);
        if (!response.ok) {
            setErrorMessage("Something went wrong. Couldn't load the seasons");
        } else {
            const data = await response.json();
            setSeasons(data);
            setLoaded(true);
        }
    };


    useEffect(() => {
        getSeries();
        getSeasons();
    }, []);

    const handleModal = () => {
        setActiveModal(!activeModal);
        getSeries();
        setSeries_id(null);
    };

    return (
        <>
            <SeriesModal
                active={activeModal}
                handleModal={handleModal}
                series_id={series_id}
                setErrorMessage={setErrorMessage}
            />

            { /* SERIES TABLE */}

            <div className="columns is-mobile is-centered">
            <div className="column">
                <p className="bd-notification is-primary">
                    <h1 style={{paddingLeft:20, fontWeight:"bolder", fontSize:40}}>Series Table</h1>
                </p>
            </div>
            </div>

            <button 
                className="button is-fullwidth is-success is-focused"

                onClick={() => handleCreate()}
            >
                Create Series
            </button>
            <ErrorMessage message={errorMessage} />
            {loaded && series ? (
                <table className="table is-fullwidth is-hoverable">
                    <thead>
                        <tr>
                            <th>Seire ID</th>
                            <th>Title</th>
                            <th>Release Date</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {series.map((series) => (
                            <tr key={series.series_id}>
                                <td>{series.series_id}</td>
                                <td>{series.title}</td>
                                <td>{series.description}</td>
                                <td>{series.release_date}</td>
                                <td>{series.cover_image}</td>
                                <td>
                                    <button
                                        className="button is-primary is-small mr-2"
                                        onClick={() => handleUpdate(series.series_id)}
                                    >
                                        Update
                                    </button>
                                    <button
                                        className="button is-danger is-small"
                                        onClick={() => handleDelete(series.series_id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}

            <hr/>

            { /* ENDSERIES TABLE */}


            <SeasonsModal
                active={activeModal}
                handleModal={handleModal}
                season_id={season_id}
                setErrorMessage={setErrorMessage}
            />

        { /* SEASON TABLE */}
        <div className="columns is-mobile is-centered">
            <div className="column">
                <p className="bd-notification is-primary">
                    <h1 style={{paddingLeft:20, fontWeight:"bolder", fontSize:40}}>Series {"<<"} Season Table</h1>
                </p>
            </div>
            </div>

            <button 
                className="button is-fullwidth is-success is-focused"

                onClick={() => handleCreate()}
            >
                Create Series
            </button>
            <ErrorMessage message={errorMessage} />
            {loaded && season ? (
                <table className="table is-fullwidth is-hoverable">
                    <thead>
                        <tr><th>SeasonID</th>
                            <th>Title</th>
                            <th>Release Date</th>
                            <th>SeriesID</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {season.map((season) => (
                            <tr key={season.episode_id}>
                                <td>{season.title}</td>
                                <td>{season.release_date}</td>
                                <td>{season.series_id}</td>
                                <td>
                                    <button
                                        className="button is-primary is-small mr-2"
                                        onClick={() => handleUpdate(season.season_id)}
                                    >
                                        Update
                                    </button>
                                    <button
                                        className="button is-danger is-small"
                                        onClick={() => handleDelete(season.season_id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Loading...</p>
            )}

            <hr/>

            { /* ENDSEASON TABLE */}

        </>
    );
};

export default Table;
